var moveteller = flow.get('moveteller');
var stop = flow.get('stop');

if(moveteller < flow.get('waypoints').waypoints.length) {
    msg.payload = flow.get('waypoints').waypoints[moveteller++];
    flow.set('destination',msg.payload);
}
else {
    //positie om terug te keren
    moveteller -= 2;
    stop = true;
}

flow.set('moveteller', moveteller);
flow.set('stop', stop);
flow.set('scanning', false);

return msg;
