var moveteller = flow.get('moveteller');
var waypoints = flow.get('waypoints');
var stop = flow.get('stop');

if(moveteller < waypoints.waypoints.length) {
    msg.payload = waypoints.waypoints[moveteller++];
    flow.set('destination',msg.payload);
}
/*else {
